glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};
in vec3 a_position;
#if USE_TEXTURE
    in vec2 a_uv0;
    out vec2 v_uv0;
#endif
in vec4 a_color;
out vec4 v_color;
void main () {
    mat4 mvp;
    #if CC_USE_MODEL
        mvp = cc_matViewProj * cc_matWorld;
    #else
        mvp = cc_matViewProj;
    #endif
    #if USE_TEXTURE
        v_uv0 = a_uv0;
    #endif
    #if USE_TINT
    #endif
    v_color = a_color;
    gl_Position = mvp * vec4(a_position, 1);
}

glsl3.frag:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
#if USE_TEXTURE
    in vec2 v_uv0;
    uniform sampler2D texture;
#endif
in vec4 v_color;
void main () {
    vec2 uv = v_uv0;
    vec4 color = texture2D(texture, uv);
    gl_FragColor = color;
}

